SET clusterType=#{clusterType},clusterName=#{clusterName}, alarmType=#{alarmType}, alarmRule=#{alarmRule},threshold=#{threshold},receiver=#{receiver},mailMode=#{mailMode},smsMode=#{smsMode},weixinMode=#{weixinMode},toBusiness=#{toBusiness},createTime=#{createTime},updateTime=#{updateTime}
WHERE id = #{id}
